import React, { useState } from 'react';
import ApiService from '../../context/ApiService.jsx'; // Stelle sicher, dass du den ApiService korrekt importierst.
import './ContactPage.css';

const apiServiceInstance = new ApiService();


function ContactFormSection() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Verwende den ApiService, um die Nachricht an das Backend zu senden und zu speichern
    apiServiceInstance.createContactMessage(formData)
      .then(response => {
        alert('Message saved successfully!');
      })
      .catch(error => {
        console.error('Error:', error);
        alert('Failed to save the message.');
      });
  };

  return (
    <section className="contact-form-section">
      <h2>Contact Us</h2>
      <form className="contact-form" onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" name="name" placeholder="Your Name" required value={formData.name} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" placeholder="Your Email" required value={formData.email} onChange={handleChange} />
        </label>
        <label>
          Subject:
          <input type="text" name="subject" placeholder="Subject" value={formData.subject} onChange={handleChange} />
        </label>
        <label>
          Message:
          <textarea name="message" placeholder="Your Message" required value={formData.message} onChange={handleChange}></textarea>
        </label>
        <button type="submit" className="submit-button">Send Message</button>
      </form>
    </section>
  );
}

export default ContactFormSection;
