// src/pages/LoginPage/LoginPage.js
import React, { useState, useContext } from 'react';
import './LoginPage.css';
import ApiService from '../../context/ApiService.jsx';  // Import the ApiService
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const { login } = useContext(AuthContext);  // Importiere login aus AuthContext

  const handleLogin = async (e) => {
    e.preventDefault();

    const handleLogin = async (e) => {
      e.preventDefault();

      try {
        const loginRequest = { username: user, password }; // Create loginRequest
        const response = await ApiService.login(loginRequest); // Call ApiService login method
        const token = response.data.token;

        // Speichere den Token in localStorage und aktualisiere den Auth-Status
        login(token); // Ruft die login-Funktion aus dem AuthContext auf
        navigate('/profile'); // Navigiere zur Profilseite

      } catch (error) {
        setError(error.response?.data?.message || 'An error occurred during login.');
      }
};

  };

  return (
    <div className="login-page">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        {error && <p className="error-message">{error}</p>}
        <label>
          Username:
          <input
            type="text"
            value={user}
            onChange={(e) => setUser(e.target.value)}
            required
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
