// src/pages/LoginPage/LoginPage.js
import React, { useState } from 'react';
import './LoginPage.css';
import ApiService from '../../context/ApiService.jsx';  // Import the ApiService
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Verwende ApiService für den Login
      const loginRequest = { username: user, password }; // Create loginRequest
      const response = await ApiService.login(loginRequest); // Call ApiService login method
      // Redirect or save the token in localStorage
      localStorage.setItem('token', response.data.token);
      

    } catch (error) {
      // Handle error (API error or network error)
      setError(error.response?.data?.message || 'An error occurred during login.');
    }
  };

  return (
    <div className="login-page">
      <form className="login-form" onSubmit={handleLogin}>
        <h2>Login</h2>
        {error && <p className="error-message">{error}</p>}
        <label>
          Username:
          <input
            type="text"
            value={user}
            onChange={(e) => setUser(e.target.value)}
            required
          />
        </label>
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <button type="submit" className="login-button">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
